import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service';
import {FormBuilder, FormGroup, Validators} from "@angular/forms"
import {Cliente} from '../../interfaces/cliente';
import { Router } from '@angular/router';

@Component({
  selector: 'app-busqueda',
  templateUrl: './busqueda.component.html',
  styleUrls: ['./busqueda.component.css']
})
export class BusquedaComponent implements OnInit {
  formulario:FormGroup;

  objeto = this.servicios.devolver();
  nombreUsuario = this.objeto.Nombre;
  comentarioSeleccionados:Array<any>=[];

  comentariosFinales:Array<any>=[];

  act = false;

  constructor(private servicios:ServicioClienteService, private router: Router, private form:FormBuilder){
    this.formulario = this.form.group({
      busqueda:['',Validators.required]
    });
  }

  ngOnInit():void{

  }


  buscarComentarios(){
    this.servicios.BuscarComentario  ({
      Dato: this.formulario.get("busqueda")?.value
    }).subscribe(respuesta => {
      console.log("valores");
      console.log(respuesta.Comentarios);


      this.servicios.BuscarComentarioSeleccionado ({
        Dato: respuesta.Comentarios
      }).subscribe(respuesta => {
        console.log("valores finales");
        console.log(respuesta.Comentarios_finales);

        this.comentariosFinales = respuesta.Comentarios_finales;



      });


    });
  }  



  navegarMostrar(){
    this.router.navigate(['/mostrar']);
  }

  navegarBuscar(){
    this.router.navigate(['/busqueda']);
  }

  navegarPerfil(){
    this.router.navigate(['/perfil']);
  }

  navegarPrincipal(){
    this.router.navigate(['/principal']);
  }


}
