import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service';
import {FormBuilder, FormGroup, Validators} from "@angular/forms"
import {Cliente} from '../../interfaces/cliente';
import { Router } from '@angular/router';

@Component({
  selector: 'app-busqueda',
  templateUrl: './busqueda.component.html',
  styleUrls: ['./busqueda.component.css']
})
export class BusquedaComponent implements OnInit {
  formulario:FormGroup;

  objeto = this.servicios.devolver();
  nombreUsuario = this.objeto.Nombre;

  constructor(private servicios:ServicioClienteService, private router: Router, private form:FormBuilder){
    this.formulario = this.form.group({
      busqueda:['',Validators.required]
    });
  }

  ngOnInit():void{

  }


  crearCliente(){
    this.servicios.CrearClientes  ({
      Registro_A: parseInt(this.formulario.get("registro_academico")?.value),
      Nombre: this.formulario.get("nombre")?.value,
      Apellidos: this.formulario.get("apellidos")?.value,
      Pass: this.formulario.get("pass")?.value,
      Correo: this.formulario.get("correo")?.value

    }).subscribe(respuesta => {
      console.log(respuesta);
    });
  }  



  navegarMostrar(){
    this.router.navigate(['/mostrar']);
  }

  navegarBuscar(){
    this.router.navigate(['/busqueda']);
  }

  navegarPerfil(){
    this.router.navigate(['/perfil']);
  }

  navegarPrincipal(){
    this.router.navigate(['/principal']);
  }


}
<!DOCTYPE html>
<html lang="en">
<head>
	<meta charset="UTF-8">
	<title>Página de mostrar</title>
</head>

<body>
    <header>
		<div class="container2">
			<img src="https://upload.wikimedia.org/wikipedia/commons/4/4a/Usac_logo.png" width="20%" > 
		</div>	
	</header>

	<div class="container3">
		<div class="btn-group" role="group" aria-label="Toolbar with button groups">
			<button (click)="navegarPrincipal()" type="button" class="btn btn-primary">Principal</button>
			<button (click)="navegarMostrar()" type="button" class="btn btn-primary">Mostrar</button>
			<button type="button" class="btn btn-primary">Buscar</button>
			<button (click)="navegarPerfil()" type="button" class="btn btn-primary">  <svg xmlns="http://www.w3.org/2000/svg" width="23" height="23" fill="currentColor" class="bi bi-person-fill" viewBox="0 0 16 16"><path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3Zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6Z"/></svg>
                [{{nombreUsuario}}]</button>
		</div>
	</div>

    <main>
		<form [formGroup]="formulario" >
			<div class="busqueda">
				<form class="d-flex">
				<input class="form-control me-2" formControlName="busqueda" type="search" placeholder="Buscar" aria-label="Search">
				<button class="btn btn-outline-success btn-sm" type="submit" style="width: 60%">Buscar</button>
				</form>
			</div>
		</form>



    </main>   

    <footer>
		<p>© 2023 - Nombre de usuario</p>
	</footer>


</body>
</html>
