import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service'
import {FormBuilder, FormGroup, Validators} from "@angular/forms"
import { Router } from '@angular/router';

@Component({
  selector: 'app-cambio-pass',
  templateUrl: './cambio-pass.component.html',
  styleUrls: ['./cambio-pass.component.css']
})
export class CambioPassComponent implements OnInit {
  formulario:FormGroup;

  constructor(private form:FormBuilder, private servicio:ServicioClienteService, private router: Router){
    this.formulario = this.form.group({
      registro_academico:['',Validators.required],
      correo: ['',Validators.required],
      pass: ['',Validators.required]
    });
  }

  ngOnInit():void{
    
  }


  modificarPass(){
    this.servicio.ModificarPass  ({
      Registro: parseInt(this.formulario.get("registro_academico")?.value),
      Correo: this.formulario.get("correo")?.value,
      NuevaPass: this.formulario.get("pass")?.value

    }).subscribe(respuesta => {

      if(respuesta.Estado == true){
        alert("Se modifico correctamente la cotraseña");
        this.router.navigate(['/home']);
      }else{
        alert("No se encuentra usuario con esos datos");
      }


      console.log(respuesta);
    });
  }  


}
<head>
    <meta charset="UTF-8">
    <title>Registrarse</title>
    
</head>
<body>
    <form [formGroup]="formulario" (submit)="modificarPass()">
        <div class="container2">
            <img src="https://plataformacii.ingenieria.usac.edu.gt/images/logo_fi.png" width="50%" > 
        </div>
        
        <div class="container2">
            <label class="labe2" for="registro_academico">REGISTRO DE USUARIOS</label>
        </div>


        <ul>
            <li>
                <label class="Label" for="registro_academico">Registro Académico:</label>
                <div class="container">
                    <input formControlName="registro_academico" type="text" id="registro_academico"  placeholder="Ingrese Registro Académico"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="correo">Correo electrónico:</label>
                <div class="container">
                    <input formControlName="correo" type="text" id="correo" placeholder="Ingrese Correo" name="correo"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="contrasena">Contraseña Nueva:</label>
                <div class="container">
                    <input formControlName="pass" type="password" id="contrasena" placeholder="Ingrese Contraseña"><br>
                </div>
            </li>
        

        </ul>
        
    
        <div class="container">
            <button type="submit"> Actualizar Contraseña </button>
            
        </div>
        
    </form>
      
</body>
