import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service';
import {Cliente} from '../../interfaces/cliente';

@Component({
  selector: 'app-mostrar',
  templateUrl: './mostrar.component.html',
  styleUrls: ['./mostrar.component.css']
})


export class MostrarComponent implements OnInit  {
  datosClientes:Array<Cliente>=[];
  constructor(private servicioCliente:ServicioClienteService){}

  ngOnInit():void{
    this.servicioCliente.ConsultarClientes().subscribe(datos=>{
      for (let i=0; i<datos.length; i++){
        this.datosClientes.push(datos[i]);
      }
    });
  }

}
