import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service'
import {FormBuilder, FormGroup, Validators} from "@angular/forms"

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  formulario:FormGroup;
  

  constructor(private form:FormBuilder, private servicio:ServicioClienteService, private router: Router){
    this.formulario = this.form.group({
      registro_academico:['',Validators.required],
      pass: ['',Validators.required]
    });
  }

  ngOnInit(){
    
  }

  navegar(){
    this.router.navigate(['/registro']);
  }

  infoLogin(){
    this.servicio.Login ({
      Registro: parseInt(this.formulario.get("registro_academico")?.value),
      Pass: this.formulario.get("pass")?.value
  
    }).subscribe(respuesta => {
      console.log(respuesta);
      
      if(!this.servicio.ingresarAplicativo(respuesta)){
        alert("No se encuentra usuario con esos datos");
        console.log(this.servicio.ingresarAplicativo(respuesta))
      } else{
        this.router.navigate(['/principal']);
        this.servicio.encapsular(respuesta.Usuario);
      }

    });

  }  


}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Login</title>
    <script src="/index.js"></script>
</head>

<body>

    
    <form [formGroup]="formulario" action="/login" method="post" (submit)="infoLogin()">
        <div class="container2">
            <img src="https://plataformacii.ingenieria.usac.edu.gt/images/logo_fi.png" width="80%" > 
        </div>
        <div class="container">
            <label for="username"><b>Usuario</b></label>
            <input  formControlName="registro_academico" type="text" placeholder="Ingrese Nombre" name="username"  id="username"required>

            <label for="password"><b>Contraseña</b></label>
            <input formControlName="pass" type="password" placeholder="Ingrese Contraseña" name="password"  id="password"  required>

            <button  type="submit"  id="ingresar">Ingresar</button>
  

            <label>
                <input type="checkbox" checked="checked" name="remember"> Recordar Contraseña
            </label>
        </div>

        <div class="container clearfix">
            <span class="psw2"><a href="/registro">Crear Usuario</a></span>
            <span class="psw"><a href="/cambio_pass">Olvide mi contraseña</a></span>
        </div>
    </form>
</body>
</html>
