import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service'
import {FormBuilder, FormGroup, Validators} from "@angular/forms"
import {Cliente} from '../../interfaces/cliente';

@Component({
  selector: 'app-registro',
  templateUrl: './registro.component.html',
  styleUrls: ['./registro.component.css']
})


export class RegistroComponent  implements OnInit {
  formulario:FormGroup;
  datoCliente:Array<Cliente>=[];
  constructor(private form:FormBuilder, private servicio:ServicioClienteService){
    this.formulario = this.form.group({
      registro_academico:['',Validators.required],
      nombre:['',Validators.required],
      apellidos: ['',Validators.required],
      pass: ['',Validators.required],
      correo: ['',Validators.required]
    });
  }



  ngOnInit():void{

  }

  crearCliente(){
    this.servicio.CrearClientes  ({
      Registro_A: parseInt(this.formulario.get("registro_academico")?.value),
      Nombre: this.formulario.get("nombre")?.value,
      Apellidos: this.formulario.get("apellidos")?.value,
      Pass: this.formulario.get("pass")?.value,
      Correo: this.formulario.get("correo")?.value

    }).subscribe(respuesta => {
      console.log(respuesta);

      if(respuesta.Estado == true){
        alert("Usuario creado correctamente");
        this.formulario.get("registro_academico")?.setValue("");
        this.formulario.get("nombre")?.setValue("");
        this.formulario.get("apellidos")?.setValue("");
        this.formulario.get("pass")?.setValue("");
        this.formulario.get("correo")?.setValue("");


      }else {
        alert("Datos inválidos para la creación");
      }

    });
  }  


}


<head>
    <meta charset="UTF-8">
    <title>Registrarse</title>

</head>
<body>
    <form [formGroup]="formulario" (submit)="crearCliente()">
        <div class="container2">
            <img src="https://plataformacii.ingenieria.usac.edu.gt/images/logo_fi.png" width="50%" > 
        </div>

        <div class="container2">
            <label class="labe2" for="registro_academico">REGISTRO DE USUARIOS</label>
        </div>


        <ul>
            <li>
                <label class="Label" for="registro_academico">Registro Académico:</label>
                <div class="container">
                    <input formControlName="registro_academico" type="text" id="registro_academico"  placeholder="Ingrese Registro Académico"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="nombres">Nombres:</label>
                <div class="container">
                    <input formControlName="nombre" type="text" id="nombres"  placeholder="Ingrese Nombre(s)"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="apellidos">Apellidos:</label>
                <div class="container">
                    <input formControlName="apellidos" type="text" id="apellidos" placeholder="Ingrese Apellidos"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="contrasena">Contraseña:</label>
                <div class="container">
                    <input formControlName="pass" type="password" id="contrasena" placeholder="Ingrese Contraseña"><br>
                </div>
            </li>

            <li>
                <label class="Label" for="correo">Correo electrónico:</label>
                <div class="container">
                    <input formControlName="correo" type="email" id="correo" placeholder="Ingrese Correo" name="correo"><br>
                </div>
            </li>

        </ul>


        <div class="container">
            <button type="submit"> Registrarse </button>

        </div>

    </form>

</body>
