import { Component, OnInit } from '@angular/core';
import {ServicioClienteService} from '../../servicios/servicio-cliente.service';
import {Cliente} from '../../interfaces/cliente';
import { Router } from '@angular/router';
import {FormBuilder, FormGroup, Validators} from "@angular/forms"

@Component({
  selector: 'app-foro',
  templateUrl: './foro.component.html',
  styleUrls: ['./foro.component.css']
})
export class ForoComponent implements OnInit {

  formulario:FormGroup;

  objeto = this.servicio.devolver();
  nombreUsuario = this.objeto.Nombre;
  idUsuario = this.objeto.Registro_A;

  comentariosFinales:Array<any>=[];
  m = false;


  comentarioPrincipal = this.servicio.devolver3();
  catedratico= this.comentarioPrincipal.Catedratico;
  curso= this.comentarioPrincipal.Curso;
  creador= this.comentarioPrincipal.Creador;
  fecha2= this.comentarioPrincipal.Fecha;
  com = this.comentarioPrincipal.Comentario;


  fechaISO = this.fecha2;
  fecha = new Date(this.fechaISO);
  fechaFormateada = this.fecha.toLocaleString('es-ES', {day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit'});



  constructor(private servicio:ServicioClienteService, private router: Router, private form:FormBuilder){
    this.formulario = this.form.group({
      comentarioSegundario:['',Validators.required],

    });
  }

  ngOnInit():void{
  }

  crearSubcomentario(){

    this.servicio.CrearSubcomentarios ({
      Comentario: this.formulario.get("comentarioSegundario")?.value,
      Id_ComentarioPri: this.comentarioPrincipal.Id,
      Nombre_Usuario: this.nombreUsuario

    }).subscribe(respuesta => {
      console.log(respuesta);

      if(respuesta.Estado == true){
        alert("Comentario creado correctamente");
      }
    });
  }  


  buscarSubcomentarios(){
    this.servicio.BuscarSubcomentarios  ({
      Dato: this.comentarioPrincipal.Id
    }).subscribe(respuesta => {
      console.log("valores");
      console.log(respuesta);
      this.comentariosFinales= respuesta.Lista;
    });
  }  


  navegarMostrar(){
    this.router.navigate(['/mostrar']);
  }

  navegarBuscar(){
    this.router.navigate(['/busqueda']);
  }

  navegarPerfil(){
    this.router.navigate(['/perfil']);
  }

  navegarPrincipal(){
    this.router.navigate(['/principal']);
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
	<meta charset="UTF-8">
	<title>Página de mostrar</title>
</head>

<body>
    <header>
		<div class="container2">
			<img src="https://upload.wikimedia.org/wikipedia/commons/4/4a/Usac_logo.png" width="20%" > 
		</div>	
	</header>

	<div class="container3">
		<div class="btn-group" role="group" aria-label="Toolbar with button groups">
			<button (click)="navegarPrincipal()" type="button" class="btn btn-primary">Principal</button>
			<button (click)="navegarMostrar()" type="button" class="btn btn-primary">Mostrar</button>
			<button (click)="navegarBuscar()" type="button" class="btn btn-primary">Buscar</button>
			<button (click)="navegarPerfil()" type="button" class="btn btn-primary">  <svg xmlns="http://www.w3.org/2000/svg" width="23" height="23" fill="currentColor" class="bi bi-person-fill" viewBox="0 0 16 16"><path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3Zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6Z"/></svg>
                [{{nombreUsuario}}]</button>
		</div>
	</div>

    <main>

		<br><br>
		<div class="comentarios">
			<h3>Publicacion</h3>
		</div>

		<div class="publicacion">
			<div class="card">
				<h5 class="card-header">Catedrático: {{catedratico}}</h5>
				<h6 class="card-header text-muted">Curso: {{curso}}</h6>
				<h6 class="card-header text-muted">Creador del post: {{creador}}</h6>
				<h6 class="card-header text-muted">Fecha de creacion del post: {{fechaFormateada }}</h6>
				<div class="card-body">

				  <p class="card-text">{{com}}</p>
				  <button (click)="buscarSubcomentarios()"class="btn btn-primary">Actualizar</button>
				</div>
			</div>

			<div *ngIf ="m==true" class="comentarios">
				<h3>Comentarios</h3>
			</div>

			<div *ngFor ="let item of comentariosFinales" >


				<div class="card">
					<div class="card-header">
					Sub-comentario
					</div>
					<div class="card-body">
					<blockquote class="blockquote mb-0">
						<p>{{item.Nombre_Usuario}}</p>
						<footer class="blockquote-footer"> <cite title="Source Title">{{item.Comentario}}</cite></footer>
					</blockquote>
					</div>
				</div>
			</div>



			<br><br>
			<div class="comentarios">
				  <h3>Crear Comentario</h3>
			</div>



			<form [formGroup]="formulario" action="/login" method="post" (submit)="crearSubcomentario()">
				<ul>
					<li>
						<div class="mb-3">
							<textarea formControlName="comentarioSegundario" class="form-control form-control-lg"	 id="exampleFormControlTextarea1" rows="3"></textarea>
						</div>
					</li>

				</ul>

				<div class="container">
					<button type="submit"> Comentar </button>
				</div>

			</form>

		</div>

    </main>   

    <footer>
        <br><br><br><br><br>
		<p>© 2023 - Nombre de usuario</p>
	</footer>


</body>
</html>
